schemas:
DEFINE TABLE OVERWRITE friends_with SCHEMAFULL TYPE RELATION IN users OUT users;

# DEFINE FIELD OVERWRITE field ON friends_with;
DEFINE TABLE OVERWRITE read_recipe SCHEMAFULL TYPE RELATION IN users OUT recipes;

# DEFINE FIELD OVERWRITE field ON read_recipe;
DEFINE TABLE OVERWRITE recipes SCHEMAFULL;

DEFINE FIELD OVERWRITE written_by ON recipes TYPE record<users>;
DEFINE FIELD OVERWRITE recipe_markdown ON recipes TYPE string;
DEFINE FIELD OVERWRITE title ON recipes TYPE string;
DEFINE FIELD OVERWRITE recipe_image ON recipes TYPE option<string> ASSERT $value.is_url();
# DEFINE FIELD OVERWRITE field ON recipes;
DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE OVERWRITE users SCHEMAFULL;

DEFINE FIELD OVERWRITE username ON users TYPE string;
DEFINE INDEX OVERWRITE unique_username ON users FIELDS username UNIQUE;
DEFINE FIELD OVERWRITE hash ON users TYPE string;
DEFINE FIELD OVERWRITE bio ON users TYPE option<string>;
DEFINE FIELD OVERWRITE avatar_url ON users TYPE option<string> ASSERT $value.is_url();
